import React, { Component } from 'react';

class Bulb extends Component {
    constructor(props) {
        super(props)
        this.state = {
            colors: ["white", "yellow"],
            lightSwitch: ["on", "off"],
            currentLightColor: "",
            currentLightSwitch: "off"
        }
    }




    handleChange = () => {
        //destructure
        let { lightSwitch, colors } = this.state

        let newColor = colors[Math.floor(Math.random() * 2)]
        let newSwitch = lightSwitch[Math.floor(Math.random() * 2)]
        if (newSwitch === "off") {
            this.setState({
                currentLightColor: "yellow", currentLightSwitch: "on"
            })

        }


        // let { colorOptions } = this.state
        // //create a var to hold the random color
        // let newColor = colorOptions[Math.floor(Math.random() * 6)]
        // // this.setState({ currentColor : newColor})
        // this.setState({ currentColor: newColor })
    }



    render() {
        const buttonStyle = {
            backgroundColor: this.state.currentLightColor,
            border: "1px, solid, #000",
            fontSize: "50px",
            height: "400px",
            width: "400px",
            padding: "30px 30px",
            margin: "50px 200px"
        }
        return (
            <div>
                <button style={buttonStyle} onClick={this.handleChange} > {this.state.currentLightSwitch}  </button>
            </div>
        )
    }
}

export default Bulb
