import React, { Component } from 'react';

class Bulb extends Component {
    constructor(props) {
        super(props)
        this.state = {
            colors: ["white", "yellow"],
            lightSwitch: ["on", "off"],
            currentLightColor: "click on me",
            currentLightSwitch: ""
        }
    }


    handleChange = () => {
        //destructure
        let { colors, lightSwitch } = this.state

        let startingLight = lightSwitch[1]
        if (startingLight = "off") {
            this.setState({ currentLightColor: "white", currentLightSwitch: "on" })
            startingLight = "on"
        }
        if (startingLight = "on") {
            this.setState({ currentLightColor: "white", currentLightSwitch: "off" })
            startingLight = "off"
        }
    }



    render() {
        const buttonStyle = {
            backgroundColor: this.state.currentLightColor,
            border: "1px, solid, #000",
            fontSize: "50px",
            height: "400px",
            width: "400px",
            padding: "30px 30px",
            margin: "50px 200px"
        }
        return (
            <div>
                <button style={buttonStyle} onClick={this.handleChange} > {this.state.currentLightColor}  </button>
            </div>
        )
    }
}

export default Bulb
